import axios from 'axios';
import PlayListItem from '../Playlist/playlistitem';
import DropdownMenu from '../../pages/favorites/dropdown';

   
function FavoritesItem(props){


      let [plist, setPlaylist] = useState([]);
      

  
  
      useEffect(()=>{
      Aos.init({duration: 2000 }); 
      },[])


    

   



