import styled from 'styled-components'


export const MovieImg = styled.div`
max-width: 1000px;
margin; 0 auto;
align-items: center;
padding: 0 50px;

@media screen and (max-width: 1000px){
    grid-template-columns: 1fr 1fr;
}

@media screen and (max-width: 768px){
    grid-template-columns: 1fr;
}
`
