import React,{ useEffect} from 'react';
import Aos from 'aos';
import "aos/dist/aos.css";
import {Link} from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown'
import axios from 'axios';

   
function FavoritesItem(props){


      let [plist, setPlaylist] = useState([]);

  
  
      useEffect(()=>{
    Aos.init({duration: 2000 }); 
},[])


     useEffect(()=>{

      const getFavs = () =>{
        axios.get('http://localhost:8070/api/favorites/allfavs').then((res)=>{
          setFavs(res.data);
        })
      }

      getFavs();
    },[])



  return(
  <div>
    <head>
    <link rel="stylesheet" href="./favorites.css" ></link>
</head>
      
       <div className='wrapper' data-aos="fade-up">
        
         
      
        <Link to={`/movie/${props.id}`}><img  className="mimg" src={props.img} alt=''/></Link>
                                        <p className="movieTitle">{props.title}  - {props.year}</p>
  
        
<Dropdown>
  <Dropdown.Toggle variant="success" id="dropdown-basic">
  Add to a PlayList
  </Dropdown.Toggle>

  <Dropdown.Menu>
    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
  </Dropdown.Menu>
</Dropdown>

           
           </div>
           

        </div>
        
  );
             
    
}      
  
    


export default FavoritesItem
